    def test_mean_function(self):
        self.assertEqual(stat.mean(TestStatFunction.data_set), 2031928999.9228272)

    def test_negative_test_mean_type_err(self):

        with self.assertRaises(TypeError) as raised_exception:
            stat.mean('not list')
        self.assertEqual(raised_exception.exception.args[0],
                         'Data set must be list or tuple.')

    def test_negative_test_mean_value_err(self):
        with self.assertRaises(ValueError) as raised_exception:
            stat.mean([])
        self.assertEqual(raised_exception.exception.args[0],
                         'Data set should have some data')

    def test_variance_function(self):
        self.assertEqual(stat.variance(TestStatFunction.data_set), 1.22741677597752e+19)

    def test_negative_varience_function(self):
        with self.assertRaises(ValueError) as raised_exception:
            stat.variance([1])
        self.assertEqual(raised_exception.exception.args[0],
                         'Data set should have minimum two elements for this function')

    def test_std_function(self):
        self.assertEqual(stat.std(TestStatFunction.data_set), 3503450835.9295125)

